mapper.select:
SELECT algh.aseCode ,algh.hospitalId ,algh.patientId ,algh.asemFactor ,algh.asemfCode ,algh.aseName ,algh.aseReaction ,algh.pDoctor ,algh.asedTime ,algh.adminId ,algh.aserDate ,pit.pName FROM allergyh algh JOIN patient pit ON algh.hospitalId = pit.hospitalId AND algh.patientId = pit.patientId WHERE algh.hospitalId = #{hospitalId}
and ${sk1} Like CONCAT('%',#{sv1},'%')
AND ${sk2} Like CONCAT('%',#{sv2},'%')
AND aserDate >= #{start_date}
AND aserDate <= #{finish_date}

SELECT alg.aseCode ,alg.hospitalId ,alg.patientId ,alg.asemFactor ,alg.asemfCode ,alg.aseName ,alg.aseReaction ,alg.pDoctor ,alg.asedTime ,alg.adminId ,alg.aserDate ,pit.pName FROM allergy alg JOIN patient pit ON alg.hospitalId = pit.hospitalId AND alg.patientId = pit.patientId
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND aserDate >= #{start_date}
AND aserDate <= #{finish_date}

mapper.delete:
DELETE
FROM allergy
WHERE
aseCode = #{aseCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
