mapper.select:
SELECT brh.ptrCode ,brh.hospitalId ,brh.patientId ,brh.ptName ,brh.ptCode ,brh.ptrValue ,brh.pDoctor ,brh.ptdTime ,brh.adminId ,brh.ptrDate ,pit.pName FROM byungrih brh JOIN patient pit ON brh.hospitalId = pit.hospitalId AND brh.patientId = pit.patientId WHERE brh.hospitalId = #{hospitalId}
and ${sk1} Like CONCAT('%',#{sv1},'%')
AND ${sk2} Like CONCAT('%',#{sv2},'%')
AND ptrDate >= #{start_date}
AND ptrDate <= #{finish_date}

SELECT br.ptrCode ,br.hospitalId ,br.patientId ,br.ptName ,br.ptCode ,br.ptrValue ,br.pDoctor ,br.ptdTime ,br.adminId ,br.ptrDate ,pit.pName FROM byungri br JOIN patient pit ON br.hospitalId = pit.hospitalId AND br.patientId = pit.patientId
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND ptrDate >= #{start_date}
AND ptrDate <= #{finish_date}

mapper.delete:
DELETE
FROM byungri
WHERE
ptrCode = #{ptrCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
