mapper.select:
SELECT dcCode ,hospitalId ,patientId ,dccName ,pDoctor ,adminId ,dcrDate FROM drinkingh WHERE hospitalId = #{hospitalId}
and ${sk} Like CONCAT('%',#{sv},'%')
AND dcrDate >= #{start_date}
AND dcrDate <= #{finish_date}

SELECT dcCode ,hospitalId ,patientId ,dccName ,pDoctor ,adminId ,dcrDate FROM drinking
WHERE ${sk} Like CONCAT('%',#{sv},'%')
AND dcrDate >= #{start_date}
AND dcrDate <= #{finish_date}

mapper.delete:
DELETE
FROM drinking
WHERE
dcCode = #{dcCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
