mapper.select:
SELECT drkh.dcCode ,drkh.hospitalId ,drkh.patientId ,drkh.dccName ,drkh.pDoctor ,drkh.adminId ,drkh.dcrDate ,pit.pName FROM drinkingh drkh JOIN patient pit ON drkh.hospitalId = pit.hospitalId AND drkh.patientId = pit.patientId WHERE drkh.hospitalId = #{hospitalId}
and ${sk1} Like CONCAT('%',#{sv1},'%')
AND ${sk2} Like CONCAT('%',#{sv2},'%')
AND dcrDate >= #{start_date}
AND dcrDate <= #{finish_date}

SELECT drk.dcCode ,drk.hospitalId ,drk.patientId ,drk.dccName ,drk.pDoctor ,drk.adminId ,drk.dcrDate ,pit.pName FROM drinking drk JOIN patient pit ON drk.hospitalId = pit.hospitalId AND drk.patientId = pit.patientId
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND dcrDate >= #{start_date}
AND dcrDate <= #{finish_date}

mapper.delete:
DELETE
FROM drinking
WHERE
dcCode = #{dcCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
