mapper.select:
SELECT gnh.ftrCode ,gnh.hospitalId ,gnh.patientId ,gnh.ftName ,gnh.ftCode ,gnh.ftrValue ,gnh.pDoctor ,gnh.ftdTime ,gnh.adminId ,gnh.ftrDate ,pit.pName FROM ginuengh gnh JOIN patient pit ON gnh.hospitalId = pit.hospitalId AND gnh.patientId = pit.patientId WHERE gnh.hospitalId = #{hospitalId}
and ${sk1} Like CONCAT('%',#{sv1},'%')
AND ${sk2} Like CONCAT('%',#{sv2},'%')
AND ftrDate >= #{start_date}
AND ftrDate <= #{finish_date}

SELECT gn.ftrCode ,gn.hospitalId ,gn.patientId ,gn.ftName ,gn.ftCode ,gn.ftrValue ,gn.pDoctor ,gn.ftdTime ,gn.adminId ,gn.ftrDate ,pit.pName FROM ginueng gn JOIN patient pit ON gn.hospitalId = pit.hospitalId AND gn.patientId = pit.patientId
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND ftrDate >= #{start_date}
AND ftrDate <= #{finish_date}

mapper.delete:
DELETE
FROM ginueng
WHERE
ftrCode = #{ftrCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
