mapper.select:
SELECT gch.strCode ,gch.hospitalId ,gch.patientId ,gch.stiName ,gch.stiCode ,gch.stName ,gch.strValue ,gch.strrValue ,gch.pDoctor ,gch.stdTime ,gch.adminId ,gch.strDate ,pit.pName FROM gumcheh gch JOIN patient pit ON gch.hospitalId = pit.hospitalId AND gch.patientId = pit.patientId WHERE gch.hospitalId = #{hospitalId}
and ${sk1} Like CONCAT('%',#{sv1},'%')
AND ${sk2} Like CONCAT('%',#{sv2},'%')
AND strDate >= #{start_date}
AND strDate <= #{finish_date}

SELECT gc.strCode ,gc.hospitalId ,gc.patientId ,gc.stiName ,gc.stiCode ,gc.stName ,gc.strValue ,gc.strrValue ,gc.pDoctor ,gc.stdTime ,gc.adminId ,gc.strDate ,pit.pName FROM gumche gc JOIN patient pit ON gc.hospitalId = pit.hospitalId AND gc.patientId = pit.patientId
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND strDate >= #{start_date}
AND strDate <= #{finish_date}

mapper.delete:
 		DELETE 
		FROM gumche
		WHERE
				     strCode = #{strCode}
			 and hospitalId = #{hospitalId}
			  and patientId = #{patientId}
