mapper.select:
SELECT strCode ,hospitalId ,patientId ,stiName ,stiCode ,stName ,strValue ,strrValue ,pDoctor ,stdTime ,adminId ,strDate FROM gumcheh WHERE hospitalId = #{hospitalId}
and ${sk} Like CONCAT('%',#{sv},'%')
AND strDate >= #{start_date}
AND strDate <= #{finish_date}

SELECT strCode ,hospitalId ,patientId ,stiName ,stiCode ,stName ,strValue ,strrValue ,pDoctor ,stdTime ,adminId ,strDate FROM gumche
WHERE ${sk} Like CONCAT('%',#{sv},'%')
AND strDate >= #{start_date}
AND strDate <= #{finish_date}

mapper.delete:
 		DELETE 
		FROM gumche
		WHERE
				     strCode = #{strCode}
			 and hospitalId = #{hospitalId}
			  and patientId = #{patientId}
