mapper.select:
SELECT jyh.lcidCode ,jyh.hospitalId ,jyh.patientId ,jyh.lcidName ,jyh.lcidpGroup ,jyh.lcidtResult ,jyh.lcidinOut ,jyh.lcidArea ,jyh.pDoctor ,jyh.lcidoDate ,jyh.lciddDate ,jyh.lcidrDate ,jyh.adminId ,jyh.lcidDate ,pit.pName FROM junyumh jyh JOIN patient pit ON jyh.hospitalId = pit.hospitalId AND jyh.patientId = pit.patientId WHERE jyh.hospitalId = #{hospitalId}
and ${sk1} Like CONCAT('%',#{sv1},'%')
AND ${sk2} Like CONCAT('%',#{sv2},'%')
AND lcidDate >= #{start_date}
AND lcidDate <= #{finish_date}

SELECT jy.lcidCode ,jy.hospitalId ,jy.patientId ,jy.lcidName ,jy.lcidpGroup ,jy.lcidtResult ,jy.lcidinOut ,jy.lcidArea ,jy.pDoctor ,jy.lcidoDate ,jy.lciddDate ,jy.lcidrDate ,jy.adminId ,jy.lcidDate ,pit.pName FROM junyum jy JOIN patient pit ON jy.hospitalId = pit.hospitalId AND jy.patientId = pit.patientId
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND lcidDate >= #{start_date}
AND lcidDate <= #{finish_date}

mapper.delete:
DELETE
FROM junyum
WHERE
lcidCode = #{lcidCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
