mapper.select:
SELECT lcidCode ,hospitalId ,patientId ,lcidName ,lcidpGroup ,lcidtResult ,lcidinOut ,lcidArea ,pDoctor ,lcidoDate ,lciddDate ,lcidrDate ,adminId ,lcidDate FROM junyumh WHERE hospitalId = #{hospitalId}
and ${sk} Like CONCAT('%',#{sv},'%')
AND lcidDate >= #{start_date}
AND lcidDate <= #{finish_date}

SELECT lcidCode ,hospitalId ,patientId ,lcidName ,lcidpGroup ,lcidtResult ,lcidinOut ,lcidArea ,pDoctor ,lcidoDate ,lciddDate ,lcidrDate ,adminId ,lcidDate FROM junyum
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND lcidDate >= #{start_date}
AND lcidDate <= #{finish_date}

mapper.delete:
DELETE
FROM junyum
WHERE
lcidCode = #{lcidCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
