mapper.select:
SELECT iidCode ,hospitalId ,patientId ,liContent ,iUrl ,idName ,pDoctor ,ifdTime ,iliDate ,adminId ,iidrDate FROM pandogh WHERE hospitalId = #{hospitalId}
and ${sk} Like CONCAT('%',#{sv},'%')
AND iidrDate >= #{start_date}
AND iidrDate <= #{finish_date}

SELECT iidCode ,hospitalId ,patientId ,liContent ,iUrl ,idName ,pDoctor ,ifdTime ,iliDate ,adminId ,iidrDate FROM pandog
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND iidrDate >= #{start_date}
AND iidrDate <= #{finish_date}

mapper.delete:
DELETE
FROM pandog
WHERE
iidCode = #{iidCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
