mapper.select:
SELECT vscCode ,hospitalId ,patientId ,vscHeight ,vscWeight ,bpMin ,bpMax ,bheat ,pDoctor ,vscCheck ,adminId ,vscrDate FROM saengchaeh WHERE hospitalId = #{hospitalId}
and ${sk} Like CONCAT('%',#{sv},'%')
AND vscrDate >= #{start_date}
AND vscrDate <= #{finish_date}

SELECT vscCode ,hospitalId ,patientId ,vscHeight ,vscWeight ,bpMin ,bpMax ,bheat ,pDoctor ,vscCheck ,adminId ,vscrDate FROM saengchae
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND vscrDate >= #{start_date}
AND vscrDate <= #{finish_date}

mapper.delete:
DELETE
FROM saengchae
WHERE
vscCode = #{vscCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
