mapper.select:
SELECT smkh.scCode ,smkh.hospitalId ,smkh.patientId ,smkh.sccName ,smkh.pDoctor ,smkh.adminId ,smkh.scrDate ,pit.pName FROM smokingh smkh JOIN patient pit ON smkh.hospitalId = pit.hospitalId AND smkh.patientId = pit.patientId WHERE smkh.hospitalId = #{hospitalId}
and ${sk1} Like CONCAT('%',#{sv1},'%')
AND ${sk2} Like CONCAT('%',#{sv2},'%')
AND scrDate >= #{start_date}
AND scrDate <= #{finish_date}

SELECT smk.scCode ,smk.hospitalId ,smk.patientId ,smk.sccName ,smk.pDoctor ,smk.adminId ,smk.scrDate ,pit.pName FROM smoking smk JOIN patient pit ON smk.hospitalId = pit.hospitalId AND smk.patientId = pit.patientId
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND scrDate >= #{start_date}
AND scrDate <= #{finish_date}

mapper.delete:
DELETE
FROM smoking
WHERE
scCode = #{scCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
