mapper.select:
SELECT sbCode ,hospitalId ,patientId ,sName ,sCode ,aKind ,pDoctor ,sdTime ,adminId ,sbDate FROM susulh WHERE hospitalId = #{hospitalId}
and ${sk} Like CONCAT('%',#{sv},'%')
AND sbDate >= #{start_date}
AND sbDate <= #{finish_date}

SELECT sbCode ,hospitalId ,patientId ,sName ,sCode ,aKind ,pDoctor ,sdTime ,adminId ,sbDate FROM susul
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND sbDate >= #{start_date}
AND sbDate <= #{finish_date}

mapper.delete:
DELETE
FROM susul
WHERE
sbCode = #{sbCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
