mapper.select:
SELECT mbCode ,hospitalId ,patientId ,mmName ,mmCode ,miName ,miCode ,mVolume ,mdUnit ,mdNumber ,mPeriod ,mUsage ,pDoctor ,mdTime ,adminId ,mrDate FROM yakmulh WHERE hospitalId = #{hospitalId}
and ${sk} Like CONCAT('%',#{sv},'%')
AND mrDate >= #{start_date}
AND mrDate <= #{finish_date}

SELECT mbCode ,hospitalId ,patientId ,mmName ,mmCode ,miName ,miCode ,mVolume ,mdUnit ,mdNumber ,mPeriod ,mUsage ,pDoctor ,mdTime ,adminId ,mrDate FROM yakmul
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND mrDate >= #{start_date}
AND mrDate <= #{finish_date}

mapper.delete:
 		DELETE 
		FROM yakmul
		WHERE
				     mbCode = #{mbCode}
			 and hospitalId = #{hospitalId}
			  and patientId = #{patientId}
