mapper.select:
SELECT ymh.mbCode ,ymh.hospitalId ,ymh.patientId ,ymh.mmName ,ymh.mmCode ,ymh.miName ,ymh.miCode ,ymh.mVolume ,ymh.mdUnit ,ymh.mdNumber ,ymh.mPeriod ,ymh.mUsage ,ymh.pDoctor ,ymh.mdTime ,ymh.adminId ,ymh.mrDate ,pit.pName FROM yakmulh ymh JOIN patient pit ON ymh.hospitalId = pit.hospitalId AND ymh.patientId = pit.patientId WHERE ymh.hospitalId = #{hospitalId}
and ${sk1} Like CONCAT('%',#{sv1},'%')
AND ${sk2} Like CONCAT('%',#{sv2},'%')
AND mrDate >= #{start_date}
AND mrDate <= #{finish_date}

SELECT ym.mbCode ,ym.hospitalId ,ym.patientId ,ym.mmName ,ym.mmCode ,ym.miName ,ym.miCode ,ym.mVolume ,ym.mdUnit ,ym.mdNumber ,ym.mPeriod ,ym.mUsage ,ym.pDoctor ,ym.mdTime ,ym.adminId ,ym.mrDate ,pit.pName FROM yakmul ym JOIN patient pit ON ym.hospitalId = pit.hospitalId AND ym.patientId = pit.patientId
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND mrDate >= #{start_date}
AND mrDate <= #{finish_date}

mapper.delete:
 		DELETE 
		FROM yakmul
		WHERE
				     mbCode = #{mbCode}
			 and hospitalId = #{hospitalId}
			  and patientId = #{patientId}
