mapper.select:
SELECT pibCode ,hospitalId ,patientId ,piName ,piCode ,pivName ,piBalance ,pDoctor ,piDate ,adminId ,pirDate FROM yebangh WHERE hospitalId = #{hospitalId}
and ${sk} Like CONCAT('%',#{sv},'%')
AND pirDate >= #{start_date}
AND pirDate <= #{finish_date}

SELECT pibCode ,hospitalId ,patientId ,piName ,piCode ,pivName ,piBalance ,pDoctor ,piDate ,adminId ,pirDate FROM yebang
WHERE ${sk1} Like CONCAT('%',#{sv1},'%') and ${sk2} Like CONCAT('%',#{sv2},'%')
AND pirDate >= #{start_date}
AND pirDate <= #{finish_date}

mapper.delete:
DELETE
FROM yebang
WHERE
pibCode = #{pibCode}
and hospitalId = #{hospitalId}
and patientId = #{patientId}
