mapper.select:
SELECT gc.strCode ,gc.hospitalId ,gc.patientId ,gc.stiName ,gc.stiCode ,gc.stName ,gc.strValue ,gc.strrValue ,gc.pDoctor ,gc.stdTime ,gc.adminId ,gc.strDate ,pit.pName FROM gumche gc JOIN patient pit ON gc.hospitalId = pit.hospitalId AND gc.patientId = pit.patientId WHERE gc.patientId = 'patientId01'
AND strDate >= #{start_date}
AND strDate <= #{finish_date}
