mapper.select:
SELECT jd.dbCode ,jd.hospitalId ,jd.patientId ,jd.dName ,jd.dCode ,jd.dDate ,jd.pDoctor ,jd.adminId ,jd.drDate ,pit.pName FROM jindan jd JOIN patient pit ON jd.hospitalId = pit.hospitalId AND jd.patientId = pit.patientId WHERE jd.patientId = 'patientId01'
AND drDate >= #{start_date}
AND drDate <= #{finish_date}
